def test_type1(): # test le type de revoie de la fonction
    assert type(tri_fusion_paire([])) == list

def test_type2(): #test le renvoie de la liste vide si le type d'entrée n'est pas une liste
    assert tri_fusion_paire(1) == []

def test_base(): #test avec une liste vide
    assert tri_fusion_paire([]) == []

def test_base_2(): #test avec une liste à un élément
    assert tri_fusion_paire([(1,1)]) == [(1,1)]

def test_base_3(): #test avec deux éléments
    assert tri_fusion_paire([(1,1), (1,1)]) == [(1,1), (1,1)]

def test_simple_1(): #test avec une liste "simple"
    assert tri_fusion_paire([(1, 2), (2, 1), (5, 4), (1, 1), (4, 2), (6, 0)]) == [(1, 1), (1, 2), (2, 1), (4, 2), (5, 4), (6, 0)]

def test_simple_2(): #test avec une liste "simple"
    assert tri_fusion_paire([(1, 1), (1, 3), (2, 1), (2, 1), (2, 1)]) == [(1, 3), (1, 1), (2, 1), (2, 1), (2, 1)]

def test_simple_3(): #test avec une liste "simple"
    assert tri_fusion_paire([(2, 1), (5, 4), (7, 1), (7, 4), (9, 0), (1, 2)]) == [(1, 2),(2, 1), (5, 4), (7, 4), (7, 1), (9, 0)]

def test_deja_trie(): #test avec une liste déjà triée
    assert tri_fusion_paire([(1, 1), (2, 1), (3, 3), (4, 4), (5, 7)]) == [(1, 1), (2, 1), (3, 3), (4, 4), (5, 7)]

def test_inverse_trie(): #test avec une liste inversement trié
    assert tri_fusion_paire([(10, 5), (8, 3), (7, 2), (1, 2)]) == [(1, 2), (7, 2), (8, 3), (10, 5)]

def test_croise(): #test croisé avec la fonction sort
    LT = tri_fusion_paire(L) #tri de la liste avec tri_fusion_paire
    PLT = []
    L.sort() #tri de la liste avec sort
    PL = []
    for i in range(len(L)): #récupération du premier élément de tout les tuples
        PLT.append(LT[i][0])
        PL.append(L[i][0])
    assert PLT == PL

